    justifyContent: 'center',
    alignItems: 'center',
    margin: 'auto',
  },
  button: {
    width: 100,
    height: 40,
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 12,
    alignItems: 'center',
    justifyContent: 'center',
  },
});
