import React from 'react';
import {  StyleSheet, Text, View } from 'react-native';


const Task = (props) => {
    return(

        <View style={styles.item}>
            <View style={styles.itemLeft}>
                <Text>{props.text}</Text>
            </View>
            <View style={styles.minusPlus}>
                <Text style={styles.minusPlusText1}>+</Text>
                <Text style={styles.minusPlusText2}>|</Text>
                <Text style={styles.minusPlusText3}>-</Text>
            </View>
           
        </View>


    )
}




const styles = StyleSheet.create({
    item: {
        backgroundColor: '#FFF',
        padding: 20,
        borderRadius: 10,
        marginTop: 10,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
    },
    itemLeft: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    minusPlus: {
        padding: 5,
        backgroundColor: '#ff5c00',
        flexDirection: 'row',
        borderRadius: 5,
        justifyContent: 'space-between',
    },
    minusPlusText1: {
        color: '#111',
        textAlign: 'center',
        fontSize: 20,
        marginLeft: 10,
        marginRight: 10,
        justifyContent: 'center',
        
    },
    minusPlusText2: {
        color: '#111',
        textAlign: 'center',
        fontSize: 20,
        marginLeft: 10,
        marginRight: 10,
    },
    minusPlusText3: {
        color: '#111',
        textAlign: 'center',
        fontSize: 20,
        marginLeft: 10,
        marginRight: 10,
    },
  
  });

export default Task;
